import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useLives } from '../contexts/LivesContext';
import { X, Upload, Heart, MessageCircle } from 'lucide-react';

const RequestLifeModal = ({ onClose, currentUserData, otherUserData, otherUserId }) => {
  const { user } = useAuth();
  const { requestLife, uploadPhoto } = useLives();
  const [message, setMessage] = useState('');
  const [photoFile, setPhotoFile] = useState(null);
  const [photoPreview, setPhotoPreview] = useState(null);
  const [loading, setLoading] = useState(false);
  const [targetUser, setTargetUser] = useState('self'); // 'self' or 'partner'

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setPhotoFile(file);
      const reader = new FileReader();
      reader.onload = (e) => setPhotoPreview(e.target.result);
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      let photoUrl = null;
      if (photoFile) {
        photoUrl = await uploadPhoto(photoFile);
      }

      const targetUserId = targetUser === 'self' ? user.id : otherUserId;
      const success = await requestLife(targetUserId, message, photoUrl);
      
      if (success) {
        onClose();
      }
    } catch (error) {
      console.error('Error requesting life:', error);
    } finally {
      setLoading(false);
    }
  };

  const getTargetUserData = () => {
    return targetUser === 'self' ? currentUserData : otherUserData;
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <button onClick={onClose} className="modal-close">
          <X size={24} />
        </button>

        <div style={{ textAlign: 'center', marginBottom: '30px' }}>
          <Heart size={50} color="#667eea" style={{ marginBottom: '15px' }} />
          <h2 style={{ color: '#667eea', marginBottom: '10px' }}>
            Request Life Usage
          </h2>
          <p style={{ color: '#666' }}>
            Both partners must approve to use a life
          </p>
        </div>

        <form onSubmit={handleSubmit}>
          {/* Target User Selection */}
          <div style={{ marginBottom: '20px' }}>
            <label style={{ display: 'block', marginBottom: '10px', fontWeight: 'bold', color: '#333' }}>
              Use life for:
            </label>
            <div style={{ display: 'flex', gap: '10px' }}>
              <button
                type="button"
                onClick={() => setTargetUser('self')}
                style={{
                  flex: 1,
                  padding: '12px',
                  border: `2px solid ${targetUser === 'self' ? '#667eea' : '#e1e5e9'}`,
                  borderRadius: '10px',
                  background: targetUser === 'self' ? '#f0f2ff' : 'white',
                  color: targetUser === 'self' ? '#667eea' : '#666',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease'
                }}
              >
                Yourself ({currentUserData.currentLives} lives)
              </button>
              <button
                type="button"
                onClick={() => setTargetUser('partner')}
                style={{
                  flex: 1,
                  padding: '12px',
                  border: `2px solid ${targetUser === 'partner' ? '#667eea' : '#e1e5e9'}`,
                  borderRadius: '10px',
                  background: targetUser === 'partner' ? '#f0f2ff' : 'white',
                  color: targetUser === 'partner' ? '#667eea' : '#666',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease'
                }}
              >
                Partner ({otherUserData.currentLives} lives)
              </button>
            </div>
          </div>

          {/* Message */}
          <div style={{ marginBottom: '20px' }}>
            <label style={{ display: 'block', marginBottom: '10px', fontWeight: 'bold', color: '#333' }}>
              <MessageCircle size={16} style={{ marginRight: '8px', verticalAlign: 'middle' }} />
              Message (optional):
            </label>
            <textarea
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              placeholder="Why do you need to use this life?"
              className="input"
              rows="3"
              style={{ resize: 'vertical' }}
            />
          </div>

          {/* Photo Upload */}
          <div style={{ marginBottom: '30px' }}>
            <label style={{ display: 'block', marginBottom: '10px', fontWeight: 'bold', color: '#333' }}>
              <Upload size={16} style={{ marginRight: '8px', verticalAlign: 'middle' }} />
              Photo (optional):
            </label>
            
            {!photoPreview ? (
              <div className="photo-upload">
                <input
                  type="file"
                  accept="image/*"
                  onChange={handlePhotoChange}
                  style={{ display: 'none' }}
                  id="photo-upload"
                />
                <label htmlFor="photo-upload" style={{ cursor: 'pointer' }}>
                  <Upload size={40} color="#667eea" style={{ marginBottom: '10px' }} />
                  <p style={{ color: '#667eea', fontWeight: 'bold' }}>
                    Click to upload a photo
                  </p>
                  <p style={{ color: '#666', fontSize: '0.9rem' }}>
                    JPG, PNG, GIF up to 5MB
                  </p>
                </label>
              </div>
            ) : (
              <div style={{ textAlign: 'center' }}>
                <img src={photoPreview} alt="Preview" className="photo-preview" />
                <button
                  type="button"
                  onClick={() => {
                    setPhotoFile(null);
                    setPhotoPreview(null);
                  }}
                  className="btn btn-danger"
                  style={{ marginTop: '10px' }}
                >
                  Remove Photo
                </button>
              </div>
            )}
          </div>

          {/* Submit Button */}
          <button
            type="submit"
            className="btn"
            disabled={loading || getTargetUserData().currentLives === 0}
            style={{ width: '100%', fontSize: '1.1rem', padding: '15px' }}
          >
            {loading ? 'Sending Request...' : 'Send Life Request'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default RequestLifeModal;
